import React, { useState, useEffect} from 'react'
import style from "./input.module.css"

export const MyInput = ({callback, placeHolder}) => {
  const [value,setValue] = useState("")

  useEffect(()=>{
    callback(value)
  },[value])

  let handleChangeValue = (e) =>{
      setValue(e.target.value)
  }
  return (
    <div>
        <input className="input" value={value} onChange={handleChangeValue} placeholder={placeHolder}/>
    </div>
  )
}
