import React, { useState, useEffect} from 'react'
import style from "./input.module.css"

export const MyInput = ({rezim}) => {
  let [valueGold,setValueGold]= useState("")
  let [valueRub,setValueRub]= useState("")
  let [sumrub, setSumRub] = useState("")
  let [sumGold, setSumGold] = useState("")

  let callbackValueGold = (e) =>{
    if(valueGold>=1000 && rezim==2){
      let suma = (6.99/1000)*valueGold;
      setSumRub(Math.floor10(suma, -2));
    }
    setValueGold(e.target.value)
    setValueRub(e.target.value)
  }
  let callbackValueRub = (e) =>{
    setValueRub(e.target.value)
  }
  useEffect(()=>{
    if(valueGold>=1000 && rezim==4){
      let suma = (12.99/1000)*valueGold;
      setSumRub(Math.floor10(suma, -2));
    }
  },[valueRub])

  useEffect(()=>{
      if(valueRub>=12.99 && rezim==4){
        let suma = (valueRub/12.99)*1000;
        setSumGold(Math.floor10(suma, -2));
      }
  },[valueGold])

  useEffect(()=>{
      if(valueRub>=6.99 && rezim==2){
        let suma = (valueRub/6.99)*1000;
        setSumGold(Math.floor10(suma, -2));
      }
  },[valueRub])

  function decimalAdjust(type, value, exp) {
    // Если степень не определена, либо равна нулю...
    if (typeof exp === 'undefined' || +exp === 0) {
      return Math[type](value);
    }
    value = +value;
    exp = +exp;
    // Если значение не является числом, либо степень не является целым числом...
    if (isNaN(value) || !(typeof exp === 'number' && exp % 1 === 0)) {
      return NaN;
    }
    // Сдвиг разрядов
    value = value.toString().split('e');
    value = Math[type](+(value[0] + 'e' + (value[1] ? (+value[1] - exp) : -exp)));
    // Обратный сдвиг
    value = value.toString().split('e');
    return +(value[0] + 'e' + (value[1] ? (+value[1] + exp) : exp));
  }
  if (!Math.floor10) {
      Math.floor10 = function(value, exp) {
        return decimalAdjust('floor', value, exp);
      }
  }
    return (
        <div className="kalk">
          <div className='continput'>
          <input className={style.gold} value={valueGold} onChange={callbackValueRub} />
          </div>     
          <div className='continput'>
            <input className={style.rub} value={valueRub} onChange={callbackValueGold} />
          </div>      
        </div>
    )
}
