import {React,useState} from 'react'
import {MyBottom} from "./UI/MyButtom/MyBottom"

export const Shethik = ({props,callback}) => {
  const [value,setValue] = useState(0)

  let dobav = () =>{

    localStorage.setItem(props,value)
    callback(false,false)
  }
  return (
    <div className="contshethik">
      <div className="shethik">
        <button className="knopshethik" onClick={()=>{
          if(value == 100){return}
          setValue(value+1)}}>+</button>
        <p>{value}</p>
        <button className="knopshethik" onClick={()=>{           
          if(value == 0){return}
          setValue(value-1)}}>-</button>
      </div>
      <div class="slidecontainer">
        <input 
        type="range" 
        min="1" 
        max="100" 
        class="slider" 
        id="myRange"
        value={value}
        onChange={(e)=>{setValue(e.target.value)}}
        />
      </div>
      <MyBottom callback={dobav} text="Положить в корзину"></MyBottom>
    </div>
  )
}
