import {React,useState} from 'react'


export const Shethik = ({props}) => {
  let [value,setValue] = useState(localStorage.getItem(props))
  let pereris = () =>{
    setValue(localStorage.getItem(props)) 
  }
  const [valueinp,setvalueinp] = useState(0)

  return (
    <div className="contshethik">
      <div className="shethik">
        <button className="knopshethik" onClick={()=>{
          if(value == 100){return}
          localStorage.setItem(props,++value);
          pereris()}}>+</button>
        <p>{
        iflocalStorage.getItem(props)
        }</p>
        <button className="knopshethik" onClick={()=>{           
          if(value == 0){return}
          console.log(value);
          localStorage.setItem(props,value-1);pereris()
        }}>-</button>
      </div>
      <div class="slidecontainer">
        <input 
        type="range" 
        min="1" 
        max="100" 
        class="slider" 
        onMouseUp={(e)=>{localStorage.setItem(props,e.target.value);pereris()}} 
        id="myRange"
        value={valueinp}
        onChange={(e)=>{setvalueinp(e.target.value)}}
        />
      </div>
    </div>
  )
}
