import React from 'react'
import { useDispatch,useSelector } from 'react-redux';

export const Shethik = ({props}) => {
  let value = useSelector(state => state[props])
  const dispatch = useDispatch()
  let slider = document.getElementById("myRange");
  let output = document.getElementById("demo");
  output.innerHTML = slider.value; // Display the default slider value
  slider.oninput = function() {
  output.innerHTML = this.value;
}
  return (
    <div className="contshethik">
      <div className="shethik">
        <button onClick={()=>{dispatch({type:props,payload:value+1})}}>+</button>
        <p>{value}</p>
        <button onClick={()=>{
        if(value == 0){return}
          dispatch({type:props,payload:value-1})
        }}>-</button>
      </div>
      <div class="slidecontainer">
        <input type="range" min="1" max="10" class="slider" value="10"id="myRange"/>
      </div>
    </div>
  )
}
