import { createStore } from 'redux';

const initialState = {
    value: 0,
    indexFocusKnopHead:0
}

export const store = createStore((state = initialState, action) => {

    switch (action.type) {

        case "increment": 
            return { ...state, value: action.payload };
            break;

        case "decrement":
            return {...state, value: action.payload};
            break;
        default:
            return state;
            break;

    }

})
