import {React,useState} from 'react'
import {MyBottom} from "./UI/MyButtom/MyBottom"

export const Shethik = ({props,callback,shethikk,kol}) => {
  const [value,setValue] = useState(0)

  let dobav = () =>{  
    localStorage.setItem(props,value)
    callback(false,false)
  }
  useEffect(()=>{
  if(shethikk==false){
    setValue(kol)
  }},[])

  if(shethikk==false){
  return (
    <div className="contshethik">
    <div className="shethik">
      <button className="knopshethik" onClick={()=>{
        if(value == 100){return}
        setValue(value+1)}}>+</button>
      <input
      type="number"
      value={value}
      onChange={(e)=>setValue(e.target.value++)}
      />
      <button className="knopshethik" onClick={()=>{           
        if(value == 0){return}
        setValue(value-1)}}>-</button>
    </div>
    <MyBottom callback={dobav} text="Положить в корзину"></MyBottom>
  </div>
  )
}
return (
      <div className="contshethik">
      <div className="shethik">
        <button className="knopshethik" onClick={()=>{
          if(value == 100){return}
          setValue(value+1)}}>+</button>
        <input
        type="number"
        value={value}
        onChange={(e)=>setValue(e.target.value++)}
        />
        <button className="knopshethik" onClick={()=>{           
          if(value == 0){return}
          setValue(value-1)}}>-</button>
      </div>
      <div class="slidecontainer">
        <input 
        type="range" 
        min="1" 
        max="100" 
        class="slider" 
        id="myRange"
        value={value}
        onChange={(e)=>{setValue(e.target.value++)}}
        />
      </div>
      <MyBottom callback={dobav} text="Положить в корзину"></MyBottom>
    </div>
)
}
