import {React,useState} from 'react'


export const Shethik = ({props}) => {
  let value = useSelector(state => state[props])
  const dispatch = useDispatch()
  const [valueinp,setvalueinp] = useState(1)

  return (
    <div className="contshethik">
      <div className="shethik">
        <button className="knopshethik" onClick={()=>{dispatch({type:props,payload:++value})}}>+</button>
        <p>{value}</p>
        <button className="knopshethik" onClick={()=>{
        if(value == 0){return}
          dispatch({type:props,payload:value-1})
        }}>-</button>
      </div>
      <div class="slidecontainer">
        <input 
        type="range" 
        min="1" 
        max="10" 
        class="slider" 
        onMouseUp={(e)=>dispatch({type:props,payload:e.target.value})} 
        id="myRange"
        value={valueinp}
        onChange={(e)=>{setvalueinp(e.target.value)}}
        />
      </div>
    </div>
  )
}
