import React, { useState, useEffect} from 'react'
import style from "./input.module.css"

export const MyInput = ({callback, placeHolder, type}) => {
  const [value,setValue] = useState("")
  const [value2,setValue2] = useState("")
  useEffect(()=>{
    callback(value)
  },[value])

  let handleChangeValue = (e) =>{
    if(type == "gold"){
      if(e.target.value > 999999){
        return
      }}
    if(type == "rub"){
      if(e.target.value > 12990){
        return
      }
    }
      setValue(e.target.value)
  }

  if(type == "gold"){
    return (
      <div>
        <input className={style.gold} value={value2} onChange={handleChangeValue} placeholder={placeHolder}/>
      </div>
    )
  }
  if(type == "rub"){
    return (
      <div>
        <input className={style.rub} value={value} onChange={handleChangeValue} placeholder={placeHolder}/>
      </div>
    )
  }

}
