import React,{ useState, useEffect} from 'react'
import {MyInput} from "./UI/MyInput/MyInput"

export const Kalkulator = ({rezim}) => {
  let [valueGold,setValue]= useState("")
  let [sumrub, setSumRub] = useState("")
  let [sumGold, setSumGold] = useState("")

  let callback = (value) =>{
    setValue(valueGold)
  }

  useEffect(()=>{
    if(value>=1000){
      let suma = (6.99/1000)*value;
      setSumRub(Math.floor10(suma, -2));
    }
  },[value])

  useEffect(()=>{
      if(value>=6.99){
        let suma = (value/6.99)*1000;
        setSumGold(Math.floor10(suma, -2));
      }
  },[value])

  function decimalAdjust(type, value, exp) {
    // Если степень не определена, либо равна нулю...
    if (typeof exp === 'undefined' || +exp === 0) {
      return Math[type](value);
    }
    value = +value;
    exp = +exp;
    // Если значение не является числом, либо степень не является целым числом...
    if (isNaN(value) || !(typeof exp === 'number' && exp % 1 === 0)) {
      return NaN;
    }
    // Сдвиг разрядов
    value = value.toString().split('e');
    value = Math[type](+(value[0] + 'e' + (value[1] ? (+value[1] - exp) : -exp)));
    // Обратный сдвиг
    value = value.toString().split('e');
    return +(value[0] + 'e' + (value[1] ? (+value[1] + exp) : exp));
  }
  if (!Math.floor10) {
      Math.floor10 = function(value, exp) {
        return decimalAdjust('floor', value, exp);
      }
  }
  return (
    <div>
      <MyInput callback={callback}/>
      <MyInput placeHolder={sumrub}/>
    </div>
  )
}
