import {React,useState} from 'react'
import { useDispatch,useSelector } from 'react-redux';


export const Shethik = ({props}) => {
  let value = useSelector(state => state[props])
  const dispatch = useDispatch()
  const [valueinp,setvalueinp] = useState("")

  return (
    <div className="contshethik">
      <div className="shethik">
        <button onClick={()=>{dispatch({type:props,payload:++value})}}>+</button>
        <p>{value}</p>
        <button onClick={()=>{
        if(value == 0){return}
          dispatch({type:props,payload:value-1})
        }}>-</button>
      </div>
      <div class="slidecontainer">
        <input 
        type="range" 
        min="1" 
        max="10" 
        class="slider" 
        onMouseUp={(e)=>dispatch({type:props,payload:e.target.value})} id="myRange"/>
      </div>
    </div>
  )
}
