import React, { useState, useEffect} from 'react'
import style from "./input.module.css"
import img from "../../../img/light/выебистая.png"
import x2 from "../../../img/light/х2.jpg"
import x4 from "../../../img/light/х4.png"
import x5 from "../../../img/light/х5.png"
import {MyBottom} from "../MyButtom/MyBottom"
import { useDispatch } from 'react-redux';

export const MyInput = ({rezim,callback,theme}) => {

  let [valueGold,setValueGold]= useState("")
  let [valueRub,setValueRub]= useState("")
  let cssrub,cssgold
  let [textknop,setTextKnop] = useState("Положить в корзину")

  if(theme == "light"){
    cssrub = style.goldlight;
    cssgold  = style.rublight;
  }
  else{
    cssrub = style.goldblack;
    cssgold  = style.rubblack;
  }

  let callbackValueGold = (e) =>{
    if(e.target.value>=6.99 && rezim==2){
      let suma = (e.target.value/6.99)*1000;
      setValueGold(Math.floor10(suma, -2));
    }
    if(e.target.value>=12.99 && rezim==4){
      let suma = (e.target.value/12.99)*1000;
      setValueGold(Math.floor10(suma, -2));
    }
    if(e.target.value>=29.99 && rezim==5){
      let suma = (e.target.value/29.99)*1000;
      setValueGold(Math.floor10(suma, -2));
    }
    setValueRub(e.target.value)
  }
  let callbackValueRub = (e) =>{
    if(e.target.value>=1000 && rezim==2){
      let suma = (6.99/1000)*e.target.value;
      setValueRub(Math.floor10(suma, -2));
    }
    if(e.target.value>=1000 && rezim==4){
      let suma = (12.99/1000)*e.target.value;
      setValueRub(Math.floor10(suma, -2));
    }
    if(e.target.value>=1000 && rezim==5){
      let suma = (29.99/1000)*e.target.value;
      setValueRub(Math.floor10(suma, -2));
    }
    setValueGold(e.target.value)
  }

  function decimalAdjust(type, value, exp) {
    // Если степень не определена, либо равна нулю...
    if (typeof exp === 'undefined' || +exp === 0) {
      return Math[type](value);
    }
    value = +value;
    exp = +exp;
    // Если значение не является числом, либо степень не является целым числом...
    if (isNaN(value) || !(typeof exp === 'number' && exp % 1 === 0)) {
      return NaN;
    }
    // Сдвиг разрядов
    value = value.toString().split('e');
    value = Math[type](+(value[0] + 'e' + (value[1] ? (+value[1] - exp) : -exp)));
    // Обратный сдвиг
    value = value.toString().split('e');
    return +(value[0] + 'e' + (value[1] ? (+value[1] + exp) : exp));
  }
  if (!Math.floor10) {
      Math.floor10 = function(value, exp) {
        return decimalAdjust('floor', value, exp);
      }
  }
  if(rezim==2){
    return (
      <div className="calcx">
      <h2 style={{"text-align":"center"}}>Цена за 1000 золота 6.99</h2>
      <div className="kalk" style={{"height":"200px"}}><img src={x2} width="200px" height="150px"></img><h2>x2</h2><MyBottom callback={()=>callback(valueGold, rezim ,valueRub)} theme={theme} text="Положить в корзину"/></div> 
      <div className="kalk">     
          <div className='continput'>
            <p>Получу</p>
            <input type="number" className={cssgold} value={valueGold} onChange={callbackValueRub} />
          </div>
          <img src={img} width="40px" />
          <div className='continput'>
            <p>Заплачу</p>
            <input type="number" className={cssrub} value={valueRub} onChange={callbackValueGold} />
          </div>      
      </div> 
     </div> 
    )}
    if(rezim==4){
      return(
      <div className="calcx">
      <h2 style={{"text-align":"center"}}>Цена за 1000 золота 12.99</h2>
      <div className="kalk" style={{"height":"200px"}}><img src={x4} width="200px" height="150px"></img><h2>x4</h2><MyBottom callback={()=>{callback(valueGold, rezim,valueRub)}} theme={theme} text="Положить в корзину"/></div> 
      <div className="kalk">     
          <div className='continput'>
            <p>Получу</p>
            <input type="number" className={cssgold} value={valueGold} onChange={callbackValueRub} />
          </div>
          <img src={img} width="40px" />
          <div className='continput'>
            <p>Заплачу</p>
            <input type="number" className={cssrub} value={valueRub} onChange={callbackValueGold} />
          </div>      
      </div> 
     </div>
      )
    }
    if(rezim==5){
      return(
      <div className="calcx"> 
      <h2 style={{"text-align":"center"}}>Цена за 1000 золота 29.99</h2>
      <div className="kalk" style={{"height":"200px"}}><img src={x5} width="200px" height="150px"></img><h2>x5</h2><MyBottom callback={()=>callback(valueGold, rezim,valueRub)} theme={theme} text="Положить в корзину"/></div> 
      <div className="kalk">
          <div className='continput'>
            <p>Получу</p>
            <input type="number" className={cssgold} value={valueGold} onChange={callbackValueRub} />
          </div>
          <img src={img} width="40px" />
          <div className='continput'>
            <p>Заплачу</p>
            <input type="number" className={cssrub} value={valueRub} onChange={callbackValueGold} />
          </div>      
      </div> 
     </div>
      )
    }
  }
