import React from 'react'
import { useDispatch,useSelector } from 'react-redux';

export const Shethik = ({tovar}) => {
  let value 
  if(tovar == voronka){
    value = useSelector(state => state.voronka)
  }

  const dispatch = useDispatch()
  return (
    <div className="contshethik">
        <button onClick={()=>{dispatch({type:"increment",payload:value+1})}}>+</button>
        <p>{value}</p>
        <button onClick={()=>{dispatch({type:"increment",payload:value-1})}}>-</button>
    </div>
  )
}
