import {React,useState,useEffect} from 'react'
import {MyBottom} from "../components/UI/MyButtom/MyBottom"
import {Shethik} from "../components/Shethik"
import {Link} from "react-router-dom";
import x2 from "../img/х2.jpg"
import x4 from "../img/х4.png"
import x5 from "../img/х5.png"
import voronka from "../img/воронка.jpg"
import pesh from "../img/песчинка.png"
import saronit from "../img/саронит.png"
import {BlockGold} from "../components/BlockGold"
import { useDispatch, useSelector } from 'react-redux';
import krestik from "../img/cross.png"
import vk from "../img/vk.png"
import tg from "../img/telegram.png"
import { GlobalStyles } from '../components/globalstyle';
import { lightTheme, darkTheme } from '../components/Themes'
import ThemeProvider from "styled-components"

const Oplata = () => {
    const [theme, setTheme] = useState('light');
    const themeToggler = () => {

    theme === 'light' ? setTheme('dark') : setTheme('light')

    }
    const sum = useSelector(state => state)
    let mas = []
    let [mastovar,setMasTovar] = useState([

    ])
    let [suma,setSum] = useState(0)
    let zx = 0

    let perech = () =>{  
      for(let key in sum ){
        if(sum[key]){
          zx += sum[key]
        }       
      }      
      setSum(Math.floor10(zx, -2))
    }

    let [deler,usedelerr] = useState("")

    let deleted = (bol) =>{
      usedelerr(bol)
    }
    useEffect(()=>perech())

    useEffect(()=>{
      mas = []
      for(let key in localStorage){     
        if (!localStorage.hasOwnProperty(key)) {
          continue; // пропустит такие ключи, как "setItem", "getItem" и так далее
        }
        let karttovar = [key,localStorage[key]]
        mas.push(karttovar)
    }},[deler])

    useEffect(()=>{
      setMasTovar(mas)
    },[deler])

    function decimalAdjust(type, value, exp) {
      // Если степень не определена, либо равна нулю...
      if (typeof exp === 'undefined' || +exp === 0) {
        return Math[type](value);
      }
      value = +value;
      exp = +exp;
      // Если значение не является числом, либо степень не является целым числом...
      if (isNaN(value) || !(typeof exp === 'number' && exp % 1 === 0)) {
        return NaN;
      }
      // Сдвиг разрядов
      value = value.toString().split('e');
      value = Math[type](+(value[0] + 'e' + (value[1] ? (+value[1] - exp) : -exp)));
      // Обратный сдвиг
      value = value.toString().split('e');
      return +(value[0] + 'e' + (value[1] ? (+value[1] + exp) : exp));
    }
    if (!Math.floor10) {
        Math.floor10 = function(value, exp) {
          return decimalAdjust('floor', value, exp);
        }
    }
    return (
      <div> 
        <ThemeProvider theme={theme === 'light' ? lightTheme : darkTheme}>
        <>
        <GlobalStyles/>

        <head className="head">
         <header className="header">
           <h2 className="logo">HG</h2>
           <div className="header_blockknop">
           <button onClick={themeToggler}>Switch Theme</button>
             <Link to="/" className="knopheader">Золото</Link>
             <Link to="/" className="knopheader">Реагенты</Link>
             <Link to="/" className="knopheader">Информация</Link>
             <Link to="/" className="knopheader">Отзывы</Link>
             <Link to="/" className="knopheader">Гарантий</Link>
           </div>
           <div class="hamburger-menu">
             <input id="menu__toggle" type="checkbox" />
             <label class="menu__btn" for="menu__toggle">
                <span></span>
             </label>
             <ul class="menu__box">
                <li><a class="menu__item" href="#">Золото</a></li>
                <li><a class="menu__item" href="#">Реагенты</a></li>
                <li><a class="menu__item" href="#">Информация</a></li>
                <li><a class="menu__item" href="#">Отзывы</a></li>
                <li><a class="menu__item" href="#">Гарантий</a></li>
             </ul>
            </div>
          </header>
        </head>
        <main>
          <din className="headotstup"></din>     
          <div className="container_korzina_content">
            <div className="block_korzina">
              <div className="tovar_korzina">
                {mastovar.map((value,index)=>{
                  let deletes = () =>{
                    localStorage.removeItem(value[0])
                    usedelerr(Math.random())
                  }
                  let imgg
                  let nameReg              
                  if(value[0]=="x2"){imgg=x2}
                  if(value[0]=="x4"){imgg=x4}
                  if(value[0]=="x5"){imgg=x5}
                  if(value[0]=="x5"||value[0]=="x4"||value[0]=="x2"){
                    let rez  = value[0]
                    return(
                      <BlockGold rezim={rez[1]} callback={deleted} props={perech}/>
                    )
                  }
                  if(value[0]=="voronka"){imgg=voronka;nameReg="Воронка пустоты"}
                  if(value[0]=="pesh"){imgg=pesh;nameReg="Песчинка потерянного времени"}
                  if(value[0]=="saronit"){imgg=saronit;nameReg="Древнейщий саронит"}
                  if(value[0]=="saronit"||value[0]=="pesh"||value[0]=="voronka"){
                    return(
                      <div style={{"width":"90%"}}>
                        <p>{nameReg}</p>
                        <div className="blocktovar" key={index}>
                          <img src={imgg} width="70px" height="70px"/>
                          <Shethik props={value[0]} shethikk={false} kol={value[1]} prop={perech}/>
                          <img src={krestik} width="30px" className='krestik' onClick={deletes}/>
                        </div>
                      </div>
                  )}})}
                  <h2>{`Итого: ${suma} рублей`}</h2>
                  <MyBottom  text="Купить" type="green"/>
              </div>    
            </div>
          </div>
        </main>
        <footer>
        <p>© 2022 HYDRAGOLD</p>
        <div className="svaz">
          <a href="https://vk.com/talgat98ufa"><img src={vk} alt="" width="50px"/></a>
          <a href=""><img src={tg} alt="" width="50px"/></a>  
        </div>   
      </footer>
      </>
        </ThemeProvider>
      </div>
    )
}
export default Oplata
