import { createStore } from 'redux';

const initialState = {
    voronka: 0,
    pesh:0,
    saronit:0
}

export const store = createStore((state = initialState, action) => {

    switch (action.type) {

        case "saronit": 
            return { ...state, value: action.payload };
            break;
        case "voronka":
            return {...state, voronka: action.payload};
            break;
        case "pesh": 
            return { ...state, pesh: action.payload };
            break;
        default:
            return state;
            break;

    }

})
